Pui.Function:
if (this.Params.Id) {
	e.Message = { 
		Service: 'dcCoreDataServices',
		Feature: 'Domains',
		Op: 'LoadDomain',
		Body: { Id: this.Params.Id }
	};
}
else {
	e.AsNew = true;
	e.Data = {
		ObscureClass: 'divconq.util.BasicSettingsObfuscator'
	};
}

if (this.Params.Id) 
	e.Data.Id = this.Params.Id;
	
// if names changed
if (e.Data.Names) {
	e.Data.Names = e.Data.Names.split(',');
	
	for (var i = 0; i < e.Data.Names.length; i++)
		e.Data.Names[i] = e.Data.Names[i].trim();
}

e.Message = { 
	Service: 'dcCoreDataServices',
	Feature: 'Domains',
	Op: this.Params.Id ? 'UpdateDomain' : 'AddDomain',
	Body: e.Data
};

var entry = this;

if (!entry.Params.Id) {
	window.history.back();
	return;
}

dc.pui.Popup.confirm('Are you sure you want to delete this Domain?', function() {
	//console.log('delete');
	dc.comm.sendMessage({ 
		Service: 'sdBackend',
		Feature: 'Domain',
		Op: 'Delete',
		Body: { 
			Id: entry.Params.Id
		}
	}, function(resp) {
		if (resp.Result > 0) {
			dc.pui.Popup.alert(resp.Message);
			return;
		}
	
		window.history.back();
	});
});
